mapper.select:
SELECT ar.adopt_request_code, ar.m_member_id, os1.os_name as osCodeAdopt, ar.animal_code, os2.os_name as osCodeAnimal, ar.adopt_request_reason, ar.adopt_request_date, ar.adopt_request_advice_date, ar.adopt_decide_date, ar.os_code_adopt FROM t_adopt_request ar
INNER JOIN t_overall_status os1 ON os1.os_code = ar.os_code_adopt INNER JOIN t_animal a ON ar.animal_code = a.animal_code INNER JOIN t_overall_status os2 ON os2.os_code = a.os_code_animal
WHERE ar.os_code_adopt =#{OsCategory}
WHERE ar.os_code_adopt =#{OsCategory}
WHERE ar.os_code_adopt =#{OsCategory}
WHERE ar.os_code_adopt =#{OsCategory}
WHERE ar.os_code_adopt =#{OsCategory}
ORDER BY ar.adopt_request_date DESC
