mapper.insert:
		INSERT INTO
			 t_board_content 
			 (
			 	board_content_code
			 	, board_code
			 	, m_member_id
			 	, board_content_title
			 	, board_content_content
			 	, board_content_date
 			) 
 			VALUES 
 			(
 				#{boardContentCode}
 				, #{boardCode}
 				, #{mMemberId}
 				, #{boardContentTitle}
 				, #{boardContentContent}
 				, now()
			)

	INSERT INTO
		 t_board 
		 (
		 	board_code
		 	, m_admin_id
		 	, board_name
		 	, board_date
	 	) 
	 	VALUES 
	 	(
	 		#{boardCode}
	 		, #{mAdminId}
	 		, #{boardName}
	 		, now()
 		)

INSERT INTO 
	t_board_reply 
	(
		board_reply_code
		, board_content_code
		, m_member_id
		, board_reply_content
		, board_reply_date
	) 
	VALUES 
	(
		#{boardReplyCode}
		, #{boardContentCode}
		, #{mMemberId}
		, #{boardReplyContent}
		, now()
	)

mapper.update:
UPDATE 
	t_board_content 
SET					
	board_content_title = #{boardContentTitle}
	, board_content_content = #{boardContentContent}
WHERE 
	board_content_code = #{boardContentCode}
