mapper.update:
UPDATE 
	t_cost_io 
SET 
	os_code=#{osCode}
WHERE 
	cost_io_code=#{costIoCode}

UPDATE 
	t_exp 
SET 
	os_code_exp=#{osCodeExp}
WHERE 
	exp_code=#{expCode}

UPDATE 
	t_animal 
SET 
	os_code_animal=#{osCodeAnimal} 
WHERE 
	animal_code=#{animalCode}

UPDATE t_exp SET
m_shelter_id_accept=#{mShelterIdAccept}, exp_check_date = #{expCheckDate},
os_code_exp=#{osCodeExp} WHERE exp_code=#{expCode}
