mapper.update:
UPDATE t_shelter_staff_request SET os_code_staff_request = #{osCodeStaffRequest}
, ssr_clear_date = now()
WHERE ssr_code = #{ssrCode}

mapper.select:
SELECT ssr_code , m_id , ssr.bl_code , bl.bl_shelter_name , os.os_name , ssr_request_date , ssr_clear_date FROM t_shelter_staff_request ssr INNER JOIN t_business_license bl ON ssr.bl_code = bl.bl_code INNER JOIN t_overall_status os ON ssr.os_code_staff_request = os.os_code
WHERE bl.bl_code=#{blCode}
WHERE m_id=#{mId}

mapper.insert:
INSERT INTO 
	t_shelter_staff_request (ssr_code, m_id, bl_code, os_code_staff_request, ssr_request_date) 
VALUES (#{ssrCode}, #{mId}, #{blCode}, #{osCodeStaffRequest}, now());

mapper.select:
SELECT bl_code , bl_shelter_name , bl_shelter_number , bl_shelter_reg_number , bl_shelter_address FROM t_business_license
WHERE os_code_license_status = #{osCodeLicenseStatus}
ORDER BY bl_shelter_name ASC

mapper.update:
UPDATE
	t_member
SET
	bl_code=#{blCode}
	, m_right_code=#{mRightCode}
WHERE
	m_id=#{mMemberId}

mapper.select:
SELECT bl_code , m_member_id , os.os_name , bl_shelter_name , bl_shelter_number , bl_shelter_reg_number , bl_shelter_address , bl_shelter_deny_reason , bl_shelter_date FROM t_business_license bl INNER JOIN t_overall_status os ON bl.os_code_license_status = os.os_code
WHERE m_member_id = #{mMemberId}
ORDER BY bl_shelter_date DESC

INSERT INTO 
	t_shelter_clinic(sc_code, bl_code, m_shelter_id, adopt_code, m_adopt_id, os_code_clinic_kind, os_code_clinic_status, sc_date, sc_reserve_date)
VALUES (#{scCode}, #{blCode}, null, #{adoptCode}, #{mAdoptId}, 'os_jindan_2_1_2', 'os_jindan_2_1_2', now(), now());
